<script>
    document.getElementById('searchInput').addEventListener('keydown', function (e) {
        if (e.key === 'Enter') {
            const query = this.value.trim();
            if (query) {
                window.location.href = `{{ route('general.product') }}?search=${encodeURIComponent(query)}`;
            }
        }
    });
</script>
